    func checkcredit_type(tempdict:[String:AnyObject]) -> String {
        var temp = ""
        if(tempdict["CreditcategoryDescrption"] as! String == "Performance" || tempdict["CreditcategoryDescrption"] as! String == "Performance Category"){
            temp = "Data input"
        }
        else if((tempdict["Mandatory"] as! String != "X") && (tempdict["CreditcategoryDescrption"] as! String != "Performance" || tempdict["CreditcategoryDescrption"] as! String != "Performance Category")){
            temp = "Base scores"
        }else if(tempdict["Mandatory"] as! String == "X"){
            temp = "Pre-requisites"
        }
        
        return temp
    }
    
    
    func checkcredentials(subscription_key:String){
        let url = NSURL.init(string:String(format: "%@assets/LEED:%d/analysis/",credentials().domain_url, NSUserDefaults.standardUserDefaults().integerForKey("leed_id")))
        //print(url?.absoluteURL)
        
        let request = NSMutableURLRequest.init(URL: url!)
        request.HTTPMethod = "GET"
        request.addValue(subscription_key, forHTTPHeaderField:"Ocp-Apim-Subscription-Key" )
        request.addValue("application/json", forHTTPHeaderField:"Content-type" )
        request.addValue(String(format:"Bearer %@",token), forHTTPHeaderField:"Authorization" )
        var task = NSURLSession.sharedSession().dataTaskWithRequest(request) { data, response, error in
            guard error == nil && data != nil else {                                                          // check for fundamental networking error
                //print("error=\(error)")
                NSNotificationCenter.defaultCenter().postNotificationName("notifyclose", object: nil)
                return
            }
            
            if let httpStatus = response as? NSHTTPURLResponse where httpStatus.statusCode != 200 {           // check for http errors
                //print("statusCode should be 200, but is \(httpStatus.statusCode)")
                //print("response = \(response)")
                dispatch_async(dispatch_get_main_queue(), {
                    self.signin()
                })
            }else{
                //print(data)
                var jsonDictionary : NSDictionary
                do {
                    jsonDictionary = try NSJSONSerialization.JSONObjectWithData(data!, options: NSJSONReadingOptions()) as! NSDictionary
                    //print(jsonDictionary)
                    dispatch_async(dispatch_get_main_queue(), {
                        
                        var subv = self.window!.subviews
                        for obj in subv {
                            obj.removeFromSuperview()
                        }
                        if(self.launched == 1){
                            var mainstoryboard = UIStoryboard.init(name: "Main", bundle: nil)
                            if(NSUserDefaults.standardUserDefaults().integerForKey("grid") == 0){
                                var v = mainstoryboard.instantiateViewControllerWithIdentifier("listofassets")
                                self.window?.rootViewController = v
                                self.validationneeded = 0
                            }else{
                                var v = mainstoryboard.instantiateViewControllerWithIdentifier("gridview")
                                self.window?.rootViewController = v
                                self.validationneeded = 0
                            }
                            
                        }else{
                            var mainstoryboard = UIStoryboard.init(name: "Main", bundle: nil)
                            if(self.vc.title != nil){
                            var v = mainstoryboard.instantiateViewControllerWithIdentifier(self.vc.title!)
                            self.window?.rootViewController = v
                            }else{
                                if(NSUserDefaults.standardUserDefaults().integerForKey("grid") == 0){
                                    var v = mainstoryboard.instantiateViewControllerWithIdentifier("listofassets")
                                    self.window?.rootViewController = v
                                    self.validationneeded = 0
                                }else{
                                    var v = mainstoryboard.instantiateViewControllerWithIdentifier("gridview")
                                    self.window?.rootViewController = v
                                    self.validationneeded = 0
                                }

                            }
                            self.validationneeded = 0
                                
                        }
                    })
                    
                    //self.tableview.reloadData()
